#!/usr/bin/perl

# 359e: https://www.reddit.com/r/dailyprogrammer/comments/8g0iil/20180430_challenge_359_easy_regular_paperfold/

use strict;
use warnings;

sub paperfold {
  my $n = @_;
  my $word = "1";

  for my $i (1..$n) {
    my $tmp = $word;
    $tmp =~ s/1/2/g;
    $tmp =~ s/0/1/g;
    $tmp =~ s/2/0/g;
    $word = $word . "1" . reverse $tmp;
  }

  return $word;
}

print paperfold(8) . "\n";

my $expected = qq{};
print $expected eq paperfold(8) ? "" : "fail";
